(()=>{const e=document.querySelector(".search-box");function t(e){let t=document.querySelector(".location .city"),n=new Date,r=document.querySelector(".location .date"),a=document.querySelector(".current .temp"),c=document.querySelector(".current .weather"),u=document.querySelector(".highTemp"),o=document.querySelector(".lowTemp");var i;null!=o?(t.innerText=`${e.name}, ${e.sys.country}`,r.innerText=`${["Sunday","Monday","Tuesday","Wednesday","Thursday","Friday","Saturday"][(i=n).getDay()]}, ${i.getDate()} ${["January","February","March","April","May","June","July","August","September","October","November","December"][i.getMonth()]} ${i.getFullYear()}`,a.innerHTML=`${Math.round(e.main.temp)}<span>°c</span>`,c.innerText=e.weather[0].main,u.innerText=`${Math.round(e.main.temp_max)}°c`,o.innerText=`${Math.round(e.main.temp_min)}°c`):alert("We couldn't find your city. Please enter city again.")}e.addEventListener("keypress",(function(n){var r;13==n.keyCode&&(r=e.value,fetch(`https://api.openweathermap.org/data/2.5/weather?q=${r}&units=metric&APPID=25e0086798dc59c52219880629e8f0c7`).then((e=>e.json())).then(t))}))})();